// This file is automatically generated. Do not edit it directly.
import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';

const SUPABASE_URL = import.meta.env.VITE_SUPABASE_URL;
const SUPABASE_PUBLISHABLE_KEY = import.meta.env.VITE_SUPABASE_PUBLISHABLE_KEY;

// Extract project ref from URL for storage key
const PROJECT_REF = SUPABASE_URL?.match(/https:\/\/([^.]+)\./)?.[1] || 'ghotrbotrywonaejlppg';
const STORAGE_KEY = `sb-${PROJECT_REF}-auth-token`;

// Custom storage adapter with logging for debugging session persistence
const customStorage = {
  getItem: (key: string) => {
    const value = window.localStorage.getItem(key);
    console.log('[Supabase Storage] getItem:', key, value ? 'found' : 'not found');
    return value;
  },
  setItem: (key: string, value: string) => {
    console.log('[Supabase Storage] setItem:', key, 'length:', value?.length);
    window.localStorage.setItem(key, value);
  },
  removeItem: (key: string) => {
    console.log('[Supabase Storage] removeItem:', key);
    window.localStorage.removeItem(key);
  },
};

// Import the supabase client like this:
// import { supabase } from "@/integrations/supabase/client";

export const supabase = createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY, {
  auth: {
    storage: customStorage,
    persistSession: true,
    autoRefreshToken: true,
    detectSessionInUrl: true,
    flowType: 'implicit'
  }
});

/**
 * Get stored access token directly from localStorage
 * This bypasses getSession() which can hang on page refresh
 */
export function getStoredAccessToken(): string | null {
  try {
    const storedSession = window.localStorage.getItem(STORAGE_KEY);
    if (storedSession) {
      const parsed = JSON.parse(storedSession);
      return parsed.access_token || null;
    }
  } catch (error) {
    console.error('[Supabase Client] Error reading stored token:', error);
  }
  return null;
}

(async () => {
  try {
    const storedSession = window.localStorage.getItem(STORAGE_KEY);
    if (storedSession) {
      const parsed = JSON.parse(storedSession);
      if (parsed?.access_token && parsed?.refresh_token) {
        console.log('[Supabase Client] Restoring session with timeout protection');

        const timeout = new Promise((_, reject) =>
          setTimeout(() => reject(new Error('Session restore timeout')), 3000)
        );

        try {
          await Promise.race([
            supabase.auth.setSession({
              access_token: parsed.access_token,
              refresh_token: parsed.refresh_token,
            }),
            timeout
          ]);
          console.log('[Supabase Client] Session restored successfully');
        } catch (e) {
          console.warn('[Supabase Client] Session restore timed out, clearing stale session');
          window.localStorage.removeItem(STORAGE_KEY);
          await supabase.auth.signOut();
        }
      }
    }
  } catch (error) {
    console.error('[Supabase Client] Error restoring session:', error);
  }
})();
